import urllib
from recipe_scrapers import scrape_me
import json
import os
import PIL
from PIL import Image
import argparse

ap = argparse.ArgumentParser()
ap.add_argument("-u", "--url", type=str,
	help="url of hellofresh webpage", default='https://www.hellofresh.nl/recipes/plaattaart-met-spinazie-en-ei-5e902343ca6b100a6e6cd1e5')
args = vars(ap.parse_args())

# give the url as a string, it can be url from any site listed below
scraper = scrape_me(args.get('url'))

title = scraper.title()
time = scraper.total_time()
yields = scraper.yields()
ingredients = scraper.ingredients()
instructions = scraper.instructions()
#utensils = scraper.utensils()
image = scraper.image()
links = scraper.links()
try:
    os.mkdir(title)
except:
    pass
with open('{}/{}.json'.format(title, title), 'w') as outfile:
    json.dump({"name":title,
               "description":"",
               "url":args.get('url'),
               "image":"\/Recipes\/{}\/full.jpg".format(title),
               "prepTime":"","cookTime":"",
               "totalTime":time,"recipeCategory":"",
               "keywords":"","recipeYield":yields,"tool":"",
               "recipeIngredient":ingredients,
               "recipeInstructions":instructions,
               "id":"",
               "@context":"http:\/\/schema.org",
               "@type":"Recipe"},
    outfile)
img = urllib.request.URLopener()
img.retrieve(image, '{}/full.jpg'.format(title))

img = Image.open('{}/full.jpg'.format(title))
img = img.resize((85, 85), PIL.Image.ANTIALIAS)
img.save('{}/thumb.jpg'.format(title))

